function NoiseColor(o,r=255){return color(`rgba(${100*noise(o+10)}%, ${100*noise(o+15)}%, ${100*noise(o+20)}%, ${r})`)}function NoiseFromColor(o,r,n=100){return color((hue(o)+map(noise(100+r),0,1,-n,n))%360,saturation(o),brightness(o))}function ComplimentColor(o){return color((hue(o)+180)%360,saturation(o),brightness(o))}function InverseColor(o){return color(`rgb(${255-round(red(o))}, ${255-round(green(o))}, ${255-round(blue(o))})`)}